function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }
	//var name = g_form.getValue('tech_name', doManager);
    var name = g_form.getReference('tech_name', doManager);
	function doManager(name)
{
	g_form.setValue("manager_name",name.manager);
	g_form.setValue("site_name",name.department);
		g_form.setValue("site_city",name.city);
		g_form.setValue("site_state",name.state);
}
}

