sp_widget.client_script:
function ($location, $rootScope) {
	var c = this;
	this.filterText = "";
	this.showFilter = false;

	this.onClick = function($event, item, url, action) {
		$event.stopPropagation();
		$event.preventDefault();
		if (url)
			$location.search(url);
		else {
			var evt = {};
			evt.url = url;
			evt.table = c.data.table;
			evt.sys_id = item.sys_id;
			evt.record = item;
			evt.rectangle_id = c.data.sys_id;
			evt.action = action;
			// put out the selection with simple list "sl_" prefix
			$location.search('sl_sys_id', evt.sys_id);
			$location.search('sl_table', evt.table);
			$location.search('spa', 1); // spa means "I've got this"
			$rootScope.$broadcast('$sp.list.click', evt);
		}
	};
}

sp_widget.script:
(function() {
	options.maximum_entries = 20;
	var gr = new GlideRecordSecure('x_ahho_supply_chai_all_requests'); // does ACL checking for us
	//var gr = new GlideRecord('x_ahho_supply_chai_all_requests');
	options.title = "My Supply Chain Requests";
	data.display_field = 'sys_created_on';
	data.secondary_fields = ['u_status','sys_updated_on'];

	gr.addEncodedQuery('u_requested_by=javascript:gs.getUserID()');
	gr.orderByDesc('sys_created_on');
	gr.query();
	data.count = gr.getRowCount();
	data.list = [];
	var recordIdx = 0;
	while (gr.next()) {
		if (recordIdx == options.maximum_entries)
			break;

		var record = {};
		record.sys_id = gr.getValue('sys_id');


			record.display_field = gr.getDisplayValue("number");


		record.secondary_fields = [];
		data.secondary_fields.forEach(function(f) {
			record.secondary_fields.push(getField(gr, f));
		});

		record.url = {id: 'form', table: 'x_ahho_supply_chai_all_requests', sys_id: record.sys_id};
		data.list.push(record);
		recordIdx++;
	}

	function getField(gr, name) {
		var f = {};
		f.display_value = gr.getDisplayValue(name);
		f.value = gr.getValue(name);
		var ge = gr.getElement(name);
		f.type = ge.getED().getInternalType()
		f.label = ge.getLabel();
		return f;
	}

})()
