sys_script_client.script:
function onLoad() {
	//Type appropriate comment here, and begin script below
	var name='';
	var sysid;
	//getting sysid of old vendor field
	name = g_form.getValue('u_vendor_name');
//supply chain application was not able to access this so made it global
	var ga = new GlideAjax('MasterVendorIntegrationMultipleWrkOrder');
    ga.addParam('sysparm_name','masterVendorMultipleWrk');
	ga.addParam('sysparm_vendor',name);
	ga.getXML(ajaxResponse);

	function ajaxResponse(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");
		//assigning old vendor field value to new vendor field
		if(g_form.getDisplayBox('u_vendor_name').value != '')
		g_form.setValue('vendor_name_multiple_wrkorder',answer);
	}
}
